import styled from "styled-components";

export const LoginFormContainer = styled.div`
    display: flex;
    justify-content: center;

    form{
        height: 541px;
        width: 460px;
        background: black;
        padding: 48px 68px;

    }
`

export const EntrarText = styled.h1`
    color: white;
    margin-bottom: 20px;
`
