import styled from "styled-components";

export const HeaderContainer = styled.header`
    height: 70px;
    width: 100%;
    background: #88F;
    padding: 0 53.953px;

    display: flex;

    color: #FFFFFF;
`

export const NavigationItemsContainer = styled.nav`
    width: 500px;
    background: red;
    margin-left: 50px;

    display: flex;
    align-items: center;
    
`

export const UsefulItemsContainer = styled.div`

`
