import styled from "styled-components";
import LogoImg from "../../../assets/logo.png"

export const HeaderContainer = styled.header`
    height: 70px;
    width: 100%;
    background: #88F;
    padding: 0 53.953px;

    display: flex;

    color: #FFFFFF;
`

export const NavigationItemsContainer = styled.nav`
    width: 500px;
    background: red;
    margin-left: 100px;

    display: flex;
    align-items: center;
    
    
`

export const UsefulItemsContainer = styled.div`
    width: 500px;
    background: blue;
    margin-left: auto;

    display: flex;
    align-items: center;
    justify-content: end;
    
`

export const Logo = styled.img.attrs(props => ({
    src: props.src || LogoImg 
}))`
    width: 190px;
    height: 75px;
    margin-left: 130px;

`
