import { EntrarText, InputButtonCodeAcess, InputButtonEntrar, InputText, LoginFormContainer, RememberMeContainer, SubscribeContainer, TestContainer } from "./styles";
import { useForm } from 'react-hook-form';
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

const LoginForm = () =>{

    const schema = yup
        .object({
            emailOrCelphoneNumber: yup.string().required("This field is required"),
            password: yup
            .string()
            .min(6, "No minimo 6 caracteres")
            .required("This field is required"),
        })
        .required();

    const { register, handleSubmit, formState: { errors, isValid } } = useForm({
        resolver: yupResolver(schema),
        mode: "onBlur",
        reValidateMode: "onChange"
    });

    const onSubmit = async (formData) =>{

        console.log(formData);
    }

    return (
        <LoginFormContainer>
            <form onSubmit={handleSubmit(onSubmit)}>
                <EntrarText>Entrar</EntrarText>

                <InputText {...register('emailOrCelphoneNumber')} type="text" placeholder="Email ou número de celular"/>
                <p>{errors?.emailOrCelphoneNumber?.message}</p>

                <InputText {...register('password')} type="password" placeholder="Senha"/>

                <InputButtonEntrar type="submit">Entrar</InputButtonEntrar>
                <p>OU</p>

                <InputButtonCodeAcess>Usar um código de acesso</InputButtonCodeAcess>

                <TestContainer>
                    <a href="google.com">Esqueceu a senha?</a><br/>
                </TestContainer>

                <RememberMeContainer>
                    <input type="radio"/><span>Lembre-se de mim</span>
                </RememberMeContainer>

                <SubscribeContainer>
                    <span className="newText">Novo por aqui?</span><a className="subscribe" href="google.com">Assine agora.</a>
                </SubscribeContainer>
                

            </form>
        </LoginFormContainer>
    )
}

export default LoginForm;
